mapper.select:

select *
from
(
select /*+INDEX(tbl_movies_comment idx_comment*/
rownum rn, combno, content, membno , stars, recommend, regdate, up_date
from tbl_movies_comment
where movbno = #{movbno} 
and rno > 0 
and rownum <= #{cri.pageNum} * #{cri.amount} 
) where rn > (#{cri.pageNum} - 1) * #{cri.amount}
